import { Button, Table } from "react-bootstrap";
import { useSelector } from "react-redux";




function Cart() {

    let a = useSelector((state) => {
        return state
    }) // redux store 가져와줌
    console.log(a.cart);
    let getCart = a.cart;

    getCart.
    return (
        <div>
            <Table>
                <thead>
                    <tr>
                    <th>#</th>
                    <th>상품명</th>
                    <th>수량</th>
                    <th>변경하기</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{getCart[0].id}</td>
                        <td>{getCart[0].name}</td>
                        <td>{getCart[0].count}</td>
                        <td><Button>버튼</Button></td>
                    </tr>
                    <tr>
                        <td>{getCart[1].id}</td>
                        <td>{getCart[1].name}</td>
                        <td>{getCart[1].count}</td>
                        <td><Button>버튼</Button></td>
                    </tr>
                    {
                        getCart.map((name, idx) => {
                            <tr>
                                <td>{ getCart[idx].id }</td>
                                <td>{ getCart[idx].name }</td>
                                <td>{ getCart[idx].count }</td>
                                <td><Button>버튼</Button></td>
                            </tr>
                        })
                    }
                </tbody>
            </Table> 
        </div>
    )
}

export default Cart;
