import { createSlice } from "@reduxjs/toolkit";


let user = createSlice({
    name: 'user',
    initialState : {name:'kim',age:20},
    //1- state   함수만들기
    reducers : {
        changeName(state) {
            state.name = 'park'
        },
        ageIncrease(state, a){
            state.age += a.payload;
        }

    }
})

export default user;
