import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from 'styled-components';



function DetailItem(props) {
    const [test, setTest] = useState(0)

    const testClick = () => {
        setTest()
    }
    // 언제 씀? : 실행시점이 약간 다르다. 렌더링 후 실행됨
    useEffect(() => {
        console.log("헬로월드");
        // for (var i = 0; i < 10000; i++){
        //     console.log(1);
        // }
        // setTimeout(() => {
        //     setTwoSecondModalSwitch(true);
        //   }, 2000);
        setTest('')
    },[buttonTest]);
    let { id } = useParams();
    let [count, setCount] = useState(0);
    let [twoSecondModalSwitch, setTwoSecondModalSwitch] = useState(false);
    const mainData = [...props.mainDisplayData];
    const fixedId = mainData.find((element) => element.id == id);

    

    

    return(
    <div className="container">
            {
                twoSecondModalSwitch == false ? <TwoSecondModal/> : null
            }
            {count}
            <button onClick={()=>{ setCount(count+1) }}>버튼</button>
            <div className="row">
                <button onClick={(e)=>testClick()}>초기화</button>
                <input onChange={(e)=>setTest(e.target.value)} type="text"/>
            <div className="col-md-6">
                    <img src={ fixedId.imges} width="80%" />
            </div>
            <div className="col-md-6">
                <h4 className="pt-5">{fixedId.title}</h4>
                <p>{fixedId.content}</p>
                <p>{fixedId.price}</p>
                <button className="btn btn-danger">주문하기</button> 
            </div>
        </div>
    </div>
    );   
}


function TwoSecondModal() {
    return (
        <div className="alert alert-warning">
            2초이내 구매시 할인
        </div>
    )
}
export default DetailItem;
