import "./App.css";
import { Container, Nav, Navbar } from "react-bootstrap/";

import reactMain from "./img/reactMain.png";
import jsImg from "./img/js.png";
import html5Img from "./img/html.png";
import cssImg from "./img/css.png";
import { useState } from "react";
import mainData from "./data";
import MainItem from "./components/MainItem";

function App() {
  let [mainDisplayData, setMainItm] = useState(mainData);
  let [imges, setImges] = useState([jsImg, html5Img, cssImg]);
  const [imgIdx, setImgIdx] = useState(0);

  return (
    <div className="App">
      <Navbar bg="dark" data-bs-theme="dark">
        <Container>
          <Navbar.Brand href="#home">리액트장인</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#Component">Component</Nav.Link>
            <Nav.Link href="#JavaScript">JavaScript</Nav.Link>
            <Nav.Link href="#HTML">HTML</Nav.Link>
            <Nav.Link href="#CSS">CSS</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <div
        //className="main-bg"
        style={{ backgroundImage: "url(" + reactMain + ")", height: "300px" }}
      ></div>
      <div className="container">
        <div className="row">
          // 컴포넌트화
          {imges.map((name, idx) => {
            console.log("idx", idx);
            return (
              <MainItem
                imgIdx={idx}
                img={imges}
                mainDisplayData={mainDisplayData}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
