import "./App.css";
import { Container, Nav, Navbar } from "react-bootstrap/";

import reactMain from "./img/reactMain.png";
import jsImg from "./img/js.png";
import html5Img from "./img/html.png";
import cssImg from "./img/css.png";
import { useState } from "react";
import mainData from "./data";
import MainItem from "./components/MainItem";
import { Routes, Route, Link } from "react-router-dom";

function App() {
  const [mainDisplayData, setMainDisplayData] = useState(mainData);
  const [imges, setImges] = useState([jsImg, html5Img, cssImg]);
  const [subject, setSubject] = useState(["자바스크립트", "HTML", "CSS"]);
  const [imgIdx, setImgIdx] = useState(0);

  // 라우터로 페이지 나누는법
  return (
    <div className="App">
      <Navbar bg="dark" data-bs-theme="dark">
        <Container>
          <Navbar.Brand href="#home">리액트장인</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/">홈</Nav.Link>
            <Nav.Link href="/detail">상세페이지</Nav.Link>
          </Nav>
        </Container>
      </Navbar>



      <Routes>
        <Route
          path="/"
          element={
            <>
              <div
                //className="main-bg"
                style={{
                  backgroundImage: "url(" + reactMain + ")",
                  height: "300px",
                }}
              ></div>
              <div className="container">
                <div className="row">
                  {
                    // 컴포넌트화
                    imges.map((name, idx) => {
                      console.log("idx", idx);
                      return (
                        <MainItem
                          idx={idx}
                          img={imges}
                          mainDisplayData={mainDisplayData}
                          subject={subject}
                        />
                      );
                    })
                  }
                </div>
              </div>
            </>
          }
        />
        <Route path="/detail" element={
          <div className="container">
          <div className="row">
            <div className="col-md-6">
              <img src="https://codingapple1.github.io/shop/shoes1.jpg" width="100%" />
            </div>
            <div className="col-md-6">
              <h4 className="pt-5">상품명</h4>
              <p>상품설명</p>
              <p>120000원</p>
              <button className="btn btn-danger">주문하기</button> 
            </div>
          </div>
        </div> 
        } />
      </Routes>
    </div>
  );
}

export default App;
