import {  Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { changeName, increase } from "../reduxData/userSlice";
import addCount from '../redux/store';

function Cart() {

    let states = useSelector((state)=> state)// redux store 가져와줌
    let dispatch = useDispatch(); // store.js로 요청보내주는 함수임
    console.log(states);
    console.log(states.cart[0]);
     
    return (
        <div>
            <h6>{states.user.name} {states.user.age}의 장바구니</h6>
            <button onClick={()=>{
                dispatch(changeName());
                dispatch(increase());
            }}>버튼</button>
            <Table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>상품명</th>
                        <th>수량</th>
                        <th>변경하기</th>
                    </tr>
                </thead>
                <tbody>
                {
                    states.cart.map((name, idx) =>   
                    <tr key={idx}>
                        <td>{ states.cart[idx].id }</td>
                        <td>{ states.cart[idx].name }</td>
                        <td>{ states.cart[idx].count}</td>
                        <td><button onClick={ ()=> dispatch(addCount(idx)) }>+</button>  
                        </td>
                    </tr>   
                    )        
                }    
                </tbody>
            </Table> 
        </div>
    )
}

export default Cart;
